import React, { useState } from 'react';
import '../css/Signup.css'; // Vamos usar um CSS básico depois, se quiser.

function Signup() {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
  });

  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/signup', { // Ajuste a URL se precisar
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        setMessage(data.message);
      } else {
        setMessage(data.message || 'Erro ao cadastrar.');
      }
    } catch (error) {
      console.error('Erro:', error);
      setMessage('Erro de conexão.');
    }
  };

  return (
    <div className="w-[400px] min-h-[500px] bg-white rounded-lg shadow-lg p-6">
        <h1 className="font-title text-3xl text-center mb-2">Signup</h1>
        <p className="text-center text-lg mb-6">Create your account to access exclusive content and more.</p>

        <form className="flex flex-col gap-4">
            <div className="flex flex-col gap-2">
                <label htmlFor="name" className="font-semibold">
                    Name
                </label>
                <input
                    type="text"
                    id="name"
                    className="border border-neutral-300 rounded-md p-2"
                    placeholder="Enter your name"
                />
            </div>

            <div className="flex flex-col gap-2">
                <label htmlFor="email" className="font-semibold">
                    Email
                </label>
                <input
                    type="email"
                    id="email"
                    className="border border-neutral-300 rounded-md p-2"
                    placeholder="Enter your email"
                />
            </div>

            <div className="flex flex-col gap-2">
                <label htmlFor="password" className="font-semibold">
                    Password
                </label>
                <input
                    type="password"
                    id="password"
                    className="border border-neutral-300 rounded-md p-2"
                    placeholder="Create a password"
                />
            </div>

            <div className="mt-4">
                <button className="w-full bg-primary text-white text-base rounded-md py-3">Signup Now</button>
            </div>

            <p className="text-xs text-center mt-2">
                We respect your privacy. Your information will never be shared with third parties.
            </p>
        </form>

        <div className="relative my-4 text-center">
            <span className="absolute inset-0 flex items-center justify-center bg-neutral-50 px-2">or</span>
            <hr className="border-neutral-300" />
        </div>

        <div className="flex gap-4 justify-center">
            <button className="bg-blue-600 w-[40px] h-[40px] rounded-full flex items-center justify-center">
                <i className="fa-brands fa-facebook"></i>
            </button>
            <button className="bg-red-600 w-[40px] h-[40px] rounded-full flex items-center justify-center">
                <i className="fa-brands fa-google"></i>
            </button>
            <button className="bg-blue-400 w-[40px] h-[40px] rounded-full flex items-center justify-center">
                <i className="fa-brands fa-twitter"></i>
            </button>
        </div>
    </div>
  );
}

export default Signup;
