import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // <-- importar useNavigate
import '../css/Signup.css'; 

function Signup() {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
  });
  const [message, setMessage] = useState('');
  const navigate = useNavigate(); // <-- usar para navegar

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:8000/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        setMessage(data.message);
        // Redirecionar para login após sucesso
        setTimeout(() => {
          navigate('/login');
        }, 1000); // espera 1 segundo para o usuário ver a mensagem
      } else {
        setMessage(data.message || 'Erro ao cadastrar.');
      }
    } catch (error) {
      console.error('Erro:', error);
      setMessage('Erro de conexão.');
    }
  };

  const handleSignInClick = (e) => {
    e.preventDefault();
    navigate('/login'); // <-- navegar para login
  };

  return (
    <div id="webcrumbs">
      {/* Seu código todo aqui... */}
      <div className="text-center mt-6">
        <p className="text-sm">
          Já tem uma conta?{' '}
          <a
            href="#"
            onClick={handleSignInClick}
            className="text-primary-600 hover:text-primary-700 font-medium hover:underline transition-all"
          >
            Sign in
          </a>
        </p>
      </div>
    </div>
  );
}

export default Signup;
